using System.Text;

namespace AAA_TEST_Console
{
    internal class Base64
    {
        public static void TestBase64()
        {
            string originalText = "Hello, World!";
            Console.WriteLine("Original Text: " + originalText);

            // Encode to Base64
            byte[] textBytes = Encoding.UTF8.GetBytes(originalText);
            string base64Encoded = Convert.ToBase64String(textBytes);
            Console.WriteLine("Base64 Encoded: " + base64Encoded);

            // Decode from Base64
            byte[] decodedBytes = Convert.FromBase64String(base64Encoded);
            string decodedText = Encoding.UTF8.GetString(decodedBytes);
            Console.WriteLine("Decoded Text: " + decodedText);

            // Base64 into Image File
            var filePath = "image_from_base64.png";
            var base64str = "data:image/png;base64,";
            var base64prefix = "data:image/png;base64,";

            var bytes = Convert.FromBase64String(base64str.Replace(base64prefix, ""));

            using (var imageFile = new FileStream(filePath, FileMode.Create))
            {
                imageFile.Write(bytes, 0, bytes.Length);
                imageFile.Flush();
            }
        }
    }
}
